mapper.update:
update order_detail
set order_id = #{password}
, prod_id = #{prod_id}
, prod_name = #{prod_name}
, prod_price = #{prod_price}
, order_count =#{order_count}
where order_id = #{order_id} and order_seq=#{order_seq}

mapper.select:
select count(*) as count
from order_detail
where prod_id =#{prod_id}
